CREATE OR REPLACE PROCEDURE schematic_lib_inativar_regra (ie_acao_p text, nr_seq_regra_p bigint, nm_usuario_p text, ds_justificativa_p text) AS $body$
BEGIN

if (ie_acao_p = 'I') then

	update	regra_schematic
	set	dt_inativacao 		= clock_timestamp(),
		nm_usuario_inativacao 	= nm_usuario_p,
		ie_situacao 		= 'I',
		ds_justificativa	= substr(ds_justificativa_p,1,255)
	where	nr_sequencia		= nr_seq_regra_p;

elsif (ie_acao_p = 'L') then

	update	regra_schematic
	set	dt_liberacao 		= clock_timestamp(),
		NM_USUARIO_LIB 		= nm_usuario_p
	where	nr_sequencia		= nr_seq_regra_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
