CREATE OR REPLACE PROCEDURE scola_obter_se_proc_edicao ( nr_seq_exame_p bigint, cd_convenio_p bigint, cd_categoria_p bigint, cd_estabelecimento_p bigint, cd_material_exame_p text, ie_existe_conta_p INOUT text, dt_referencia_p timestamp default clock_timestamp(), ie_tipo_atendimento_p bigint default 0) AS $body$
DECLARE


cd_procedimento_w	bigint;
ie_origem_proced_w	smallint;
ds_retorno_w		varchar(1);
cd_exame_w		varchar(20);
nr_seq_exame_w		bigint;
cd_setor_w		bigint;
ds_erro_w		varchar(255);
nr_seq_proc_interno_aux_w bigint;
			

BEGIN

ds_retorno_w	:= 'N';

/*
select	max(cd_exame)
into	cd_exame_w
from	exame_laboratorio
where	nr_seq_exame = nr_seq_exame_p;

Obter_Proc_Exame_Laborat(	cd_exame_w,
				cd_convenio_p,
				cd_estabelecimento_p,
				cd_categoria_p,
				cd_procedimento_w,
				ie_origem_proced_w,
				nr_seq_exame_w,
				0);*/
				
				
SELECT * FROM Obter_Exame_Lab_Convenio(nr_seq_exame_p, cd_convenio_p, cd_categoria_p, ie_tipo_atendimento_p, cd_estabelecimento_p, 0, 0, cd_material_exame_p, null, cd_setor_w, cd_procedimento_w, ie_origem_proced_w, ds_erro_w, nr_seq_proc_interno_aux_w) INTO STRICT cd_setor_w, cd_procedimento_w, ie_origem_proced_w, ds_erro_w, nr_seq_proc_interno_aux_w;
				

/*select	max(a.cd_procedimento),
	max(a.ie_origem_proced)
into	cd_procedimento_w,
	ie_origem_proced_w
from	exame_laboratorio a
where	a.nr_seq_exame = nr_seq_exame_p;*/
CALL GERAR_CONSULTA_PRECO(	cd_estabelecimento_p,
			cd_convenio_p,
			dt_referencia_p,
			cd_procedimento_w,
			ie_origem_proced_w,
			0,
			NULL,
			ie_tipo_atendimento_p,
			NULL,
			NULL,
			NULL,
			NULL,
			nr_seq_exame_p,								
			NULL,
			NULL,
			NULL,
			NULL,
			NULL,
			NULL,
			NULL,
			NULL,
			NULL,
			'SCOLA',
			NULL,
			NULL,
			'A',
			1,
			'N',
			'N',
			'N',
			'N',
			cd_categoria_p,
			null,
			null,
			null,
			null,
			null,
            null);
			
select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ds_retorno_w
from	w_consulta_preco
where	ie_tipo_item = 0
and	coalesce(vl_procedimento,0) > 0
and	nm_usuario = 'SCOLA'
and	cd_procedimento 	= cd_procedimento_w
and	ie_origem_proced 	= ie_origem_proced_w
and	nr_seq_exame		= nr_seq_exame_p
and	cd_convenio     	= cd_convenio_p
and	cd_categoria		= coalesce(cd_categoria_p,cd_categoria)
and	cd_estabelecimento 	= cd_estabelecimento_p;

if (ds_retorno_w = 'N') then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	exame_lab_convenio a
	where	a.nr_seq_exame = nr_seq_exame_p
	and	a.cd_convenio = cd_convenio_p
	and	coalesce(a.cd_categoria,cd_categoria_p) = cd_categoria_p
	and	dt_referencia_p between coalesce(a.dt_inicio_vigencia, dt_referencia_p) and coalesce(a.dt_fim_vigencia, dt_referencia_p)
	and 	a.ie_situacao = 'A'
	and coalesce(a.ie_tipo_atendimento,coalesce(ie_tipo_atendimento_p,0)) = coalesce(ie_tipo_atendimento_p,0);
end if;

ie_existe_conta_p	:= ds_retorno_w;

---return	obter_se_proc_edicao(cd_estabelecimento_p,cd_convenio_p,cd_categoria_p,sysdate,cd_procedimento_w,ie_origem_proced_w);

--return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
