CREATE OR REPLACE PROCEDURE score_flex_2_obter_pontuacao (nr_seq_avaliacao_p bigint , nr_seq_escala_p bigint) AS $body$
BEGIN
    UPDATE escala_eif_ii
    SET    qt_pontos = (SELECT qt_resultado
                        FROM   score_avaliacao_result
                        WHERE  nr_seq_avaliacao = nr_seq_avaliacao_p
                           AND nr_seq_item IN (SELECT nr_seq_aval_pont
                                               FROM   eif_escala_ii eif
                                               WHERE  nr_sequencia = nr_seq_escala_p))
    WHERE  nr_sequencia = nr_seq_avaliacao_p;

    COMMIT;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
