CREATE OR REPLACE PROCEDURE sec_incluir_med_cliente (cd_pessoa_fisica_p text, cd_medico_p text, nm_usuario_p text, ie_commit_p text default 'S', nr_seq_cliente_p INOUT bigint DEFAULT NULL) AS $body$
DECLARE



nr_sequencia_w		bigint := 0;
ie_nivel_atencao_w	varchar(1);


BEGIN

ie_nivel_atencao_w := wheb_assist_pck.get_nivel_atencao_perfil();

if (ie_nivel_atencao_w = 'S') then
if (length(cd_medico_p) > 0) then
	begin

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from	med_cliente
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	cd_medico		= cd_medico_p;

	if (nr_sequencia_w	= 0) then
		begin

		select	nextval('med_cliente_seq')
		into STRICT	nr_sequencia_w
		;

		insert	into med_cliente(nr_sequencia,
			cd_medico,
			cd_pessoa_fisica,
			dt_atualizacao,
			nm_usuario,
			ie_situacao,
			dt_primeira_consulta,
			dt_ultima_consulta,
			dt_ultima_atualiz,
			cd_pessoa_sist_orig,
			cd_convenio,
			cd_usuario_convenio,
			ds_encaminhamento,
			dt_validade_carteira,
			ie_ficha_papel,
			ie_exame_consultorio,
			dt_ultima_visualiz,
			ie_exame_virtual,
			nr_seq_plano,
			ds_observacao,
			ie_gemelar,
			ie_pais_separados)
		values (nr_sequencia_w,
			cd_medico_p,
			cd_pessoa_fisica_p,
			clock_timestamp(),
			nm_usuario_p,
			'A', null, null, null,
			null, null, null, null,
			null, 'N', 'N', null, 'N',
			null, null, 'N', 'N');

			nr_seq_cliente_p := nr_sequencia_w;

		end;
	end if;
	end;
end if;

if (ie_commit_p = 'S') then
	commit;
end if;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
