CREATE OR REPLACE PROCEDURE selecionar_fator_relacionado ( nr_prescricao_p bigint, nr_seq_diag_p bigint, nr_seq_fator_p bigint, nr_seq_compl_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_existe_w			bigint;
nr_sequencia_w			bigint;
nr_sequencia_ww			bigint;
ie_forma_selecao_w		varchar(1);
qt_result_w			bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	pe_prescr_diag
where	nr_seq_prescr	= nr_prescricao_p
and	nr_seq_diag	= nr_seq_diag_p;

if (qt_existe_w = 0) then

	select	nextval('pe_prescr_diag_seq')
	into STRICT	nr_sequencia_w
	;

	insert into pe_prescr_diag(
		NR_SEQUENCIA,
		DT_ATUALIZACAO,
		NM_USUARIO,
		NR_SEQ_DIAG,
		NR_SEQ_PRESCR)
	values (	nr_sequencia_w,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_diag_p,
		nr_prescricao_p);

	insert into pe_prescr_diag_fat_rel(
		NR_SEQUENCIA,
		NR_SEQ_DIAG,
		DT_ATUALIZACAO,
		NM_USUARIO,
		NR_SEQ_FAT_REL,
		NR_SEQ_COMPL)
	SELECT	nextval('pe_prescr_diag_fat_rel_seq'),
		nr_sequencia_w,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_fator_p,
		CASE WHEN nr_seq_compl_p=0 THEN null  ELSE nr_seq_compl_p END
	
	where	not exists (	SELECT	1
				from	pe_prescr_diag_fat_rel b,
					pe_prescr_diag a
				where	a.nr_seq_prescr	= nr_prescricao_p
				and	a.nr_sequencia	= b.nr_seq_diag
				and	a.nr_seq_diag	= nr_seq_diag_p
				and	b.nr_seq_fat_rel= nr_seq_fator_p
				and	b.nr_seq_compl	= nr_seq_compl_p);

else
	select	nr_sequencia
	into STRICT	nr_sequencia_w
	from	pe_prescr_diag
	where	nr_seq_prescr	= nr_prescricao_p
	and	nr_seq_diag	= nr_seq_diag_p;

	insert into pe_prescr_diag_fat_rel(
		NR_SEQUENCIA,
		NR_SEQ_DIAG,
		DT_ATUALIZACAO,
		NM_USUARIO,
		NR_SEQ_FAT_REL,
		NR_SEQ_COMPL)
	SELECT	nextval('pe_prescr_diag_fat_rel_seq'),
		nr_sequencia_w,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_fator_p,
		CASE WHEN nr_seq_compl_p=0 THEN null  ELSE nr_seq_compl_p END
	
	where	not exists (	SELECT	1
				from	pe_prescr_diag_fat_rel b,
					pe_prescr_diag a
				where	a.nr_seq_prescr	= nr_prescricao_p
				and	a.nr_sequencia	= b.nr_seq_diag
				and	a.nr_seq_diag	= nr_seq_diag_p
				and	b.nr_seq_fat_rel= nr_seq_fator_p
				and	b.nr_seq_compl	= nr_seq_compl_p);

end if;

commit;

END;
$body$
