SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE send_history_to_users (ds_users_receiver_p text, ds_history_p text, cd_establishment_p bigint, nr_seq_history_p bigint, nm_user_p text) AS $body$
DECLARE

 
nr_seq_prospect_w	bigint;
nr_seq_comunic_w	bigint;
nr_seq_text_w		varchar(10);
ds_text_ci_w		varchar(4000);
ds_prospect_data_w	varchar(400);
ds_client_w		varchar(255);
ds_person_w		varchar(80);
			

BEGIN 
 
if (coalesce(nr_seq_history_p,0) > 0) then 
 
	select	nr_seq_prospect 
	into STRICT	nr_seq_prospect_w 
	from	cml_prospect_hist 
	where	nr_sequencia = nr_seq_history_p;
	 
	select	max(ds_cliente), 
		max(substr(CASE WHEN coalesce(cd_pessoa_fisica::text, '') = '' THEN  obter_nome_pf_pj(null, cd_cnpj)  ELSE obter_nome_pf_pj(cd_pessoa_fisica, null) END ,1,80)) 
	into STRICT	ds_client_w, 
		ds_person_w 
	from	cml_prospect 
	where	nr_sequencia = nr_seq_prospect_w;
	 
	ds_prospect_data_w	:= '\par ' || wheb_mensagem_pck.get_texto(342186) || ': ' || ds_client_w || '\par ' || wheb_mensagem_pck.get_texto(342189) || ': ' || ds_person_w || '\par ';
	 
	ds_text_ci_w		:= replace(ds_history_p, '\par }', ds_prospect_data_w || '\par }');
 
	nr_seq_comunic_w := enviar_ci_hist_prospect(ds_text_ci_w, nm_user_p, ds_users_receiver_p, cd_establishment_p, null, nr_seq_comunic_w);
	 
	if (coalesce(nr_seq_comunic_w,0) > 0) then 
		CALL enviar_ci_anexo_prospect(nm_user_p, nr_seq_prospect_w, nr_seq_comunic_w);
	end if;
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
