CREATE OR REPLACE PROCEDURE send_patient_deletion ( cd_pessoa_fisica_p text, nm_usuario_dest_p text, ds_tables_error_p text) AS $body$
DECLARE

ds_erro_w	varchar(4500);

BEGIN
	if (coalesce(ds_tables_error_p::text, '') = '') then
		ds_erro_w := wheb_mensagem_pck.get_texto(995772, 'CD_PATIENT='||cd_pessoa_fisica_p);
	else
    ds_erro_w := wheb_mensagem_pck.get_texto(995774, 'CD_PATIENT='||cd_pessoa_fisica_p||';'||'NM_TABLES='||ds_tables_error_p);
	end if;
	insert	into comunic_interna(
			DT_COMUNICADO,
			DS_TITULO,
			DS_COMUNICADO,
			NM_USUARIO,
			DT_ATUALIZACAO,
			IE_GERAL,
			NM_USUARIO_DESTINO,
			NR_SEQUENCIA,
			IE_GERENCIAL,
			DT_LIBERACAO)
		values (clock_timestamp(),
			wheb_mensagem_pck.get_texto(997627),
			ds_erro_w,
			nm_usuario_dest_p,
			clock_timestamp(),
			'N',
			nm_usuario_dest_p,
			nextval('comunic_interna_seq'),
			'N',
			clock_timestamp());
	commit;
end;
