CREATE OR REPLACE PROCEDURE separar_lp_item_nao_requi ( nr_lote_producao_p bigint, nr_seq_lote_fornec_p bigint, cd_material_p bigint, qt_material_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, cd_setor_atendimento_p bigint) AS $body$
DECLARE

 
cd_unidade_medida_consumo_w	varchar(30);
cd_local_estoque_w		smallint;
ie_baixa_estoque_w		varchar(15);
				

BEGIN 
 
ie_baixa_estoque_w := obter_param_usuario(9044, 8, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_baixa_estoque_w);
 
if (cd_material_p > 0) then 
	 
	select	cd_local_estoque 
	into STRICT	cd_local_estoque_w 
	from	lote_producao 
	where	nr_lote_producao	=	nr_lote_producao_p;
	 
	select	substr(obter_dados_material_estab(cd_material,cd_estabelecimento_p,'UMS'),1,30) cd_unidade_medida_consumo 
	into STRICT	cd_unidade_medida_consumo_w 
	from	material 
	where	cd_material 		= cd_material_p;
	 
	update	lp_item_util 
	set	qt_material		=	qt_material_p, 
		nr_seq_lote_fornec	=	nr_seq_lote_fornec_p 
	where	nr_lote_producao	=	nr_lote_producao_p 
	and	cd_material		=	cd_material_p 
	and	qt_material		=	0;
	if (NOT FOUND) then 
		begin 
		insert into lp_item_util( 
			nr_sequencia, 
			dt_atualizacao, 
			nm_usuario, 
			dt_atualizacao_nrec, 
			nm_usuario_nrec, 
			nr_lote_producao, 
			cd_material, 
			cd_unidade_medida, 
			qt_material, 
			nr_seq_lote_fornec) 
		values ( 
			nextval('lp_item_util_seq'), 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			nr_lote_producao_p, 
			cd_material_p, 
			cd_unidade_medida_consumo_w, 
			qt_material_p, 
			nr_seq_lote_fornec_p);
		end;
	end if;
 
	if (ie_baixa_estoque_w = 'S') then 
		CALL Gerar_Prescricao_Estoque(	cd_estabelecimento_p,null,null, cd_material_p,clock_timestamp(),1,cd_local_estoque_w, 
						qt_material_p,cd_setor_atendimento_p,cd_unidade_medida_consumo_w,nm_usuario_p,'I',null,null,null,null,null,null, 
						nr_seq_lote_fornec_p, null, null, null, 'X', null, nr_lote_producao_p);
	end if;
 
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
