CREATE OR REPLACE PROCEDURE separar_nome_pj_mx ( ds_razao_social_p text, ds_primeiro_nome_p INOUT text, ds_segundo_nome_p INOUT text, ds_terceiro_nome_p INOUT text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_razao_social_w			pessoa_juridica.ds_razao_social%type;
ds_letra_w				varchar(255);
ds_primeiro_nome_w			varchar(255)	:= '';
ds_segundo_nome_w			varchar(255)	:= '';
ds_terceiro_nome_w			varchar(255)	:= '';
qt_nome_w				bigint	:= 1;


BEGIN
ds_razao_social_w	:= ds_razao_social_p;

while(ds_razao_social_w IS NOT NULL AND ds_razao_social_w::text <> '') loop
	begin
	ds_letra_w	:= trim(both substr(ds_razao_social_w,1,1));

	if (ds_letra_w IS NOT NULL AND ds_letra_w::text <> '') then
		if (qt_nome_w = 1) then
			ds_primeiro_nome_w	:= ds_primeiro_nome_w || ds_letra_w;
		elsif (qt_nome_w = 2) then
			ds_segundo_nome_w	:= ds_segundo_nome_w || ds_letra_w;
		elsif (qt_nome_w = 3) then
			ds_terceiro_nome_w	:= ds_terceiro_nome_w || ds_letra_w;
		end if;
	else
		qt_nome_w	:= qt_nome_w + 1;
	end if;

	ds_razao_social_w	:= substr(ds_razao_social_w,2,255);
	end;
end loop;

ds_primeiro_nome_p	:= ds_primeiro_nome_w;
ds_segundo_nome_p	:= ds_segundo_nome_w;
ds_terceiro_nome_p	:= ds_terceiro_nome_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
