CREATE OR REPLACE PROCEDURE seta_via_princ_proc_maior_vl (nr_cirurgia_p bigint) AS $body$
DECLARE


nr_sequencia_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from 	procedimento_paciente
	where	nr_cirurgia = nr_cirurgia_p
	and	vl_medico = (SELECT max(vl_medico) from procedimento_paciente where nr_cirurgia = nr_cirurgia_p);


BEGIN


open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	update	procedimento_paciente
	set	ie_via_acesso 	= 'U'
	where	nr_sequencia 	= nr_sequencia_w;
	end;
end loop;
close c01;
commit;

end	;
