SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE set_adv_appointment_value (insert_or_update_p text, schedule_type_p text, nr_seq_p bigint, ie_adv_appointment_p text) AS $body$
BEGIN

	if (schedule_type_p = 'C' and insert_or_update_p = 'I') then
	
		insert into agenda_consulta_adic(nr_seq_agenda,
			nm_usuario,
			dt_atualizacao,
			ie_adv_appointment)
		values (nr_seq_p,
			wheb_usuario_pck.get_nm_usuario,
			clock_timestamp(),
			ie_adv_appointment_p);
		commit;
		
	elsif (schedule_type_p = 'C' and insert_or_update_p = 'U') then
	
		update	agenda_consulta_adic
		set	ie_adv_appointment = ie_adv_appointment_p
		where	nr_seq_agenda = nr_seq_p;
		commit;

	elsif (schedule_type_p = 'E' and insert_or_update_p = 'I') then

		insert into agenda_paciente_auxiliar(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			ie_adv_appointment)
		values (nr_seq_p,
			clock_timestamp(),
			wheb_usuario_pck.get_nm_usuario,
			ie_adv_appointment_p);
		commit;

	elsif (schedule_type_p = 'E' and insert_or_update_p = 'U') then

		update	agenda_paciente_auxiliar
		set	ie_adv_appointment = ie_adv_appointment_p
		where	nr_sequencia = nr_seq_p;
		commit;

	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
