CREATE OR REPLACE PROCEDURE set_consent_agenda_cirurgia ( nr_seq_consentimento_p pep_pac_ci.nr_sequencia%type, ie_tipo_termo_cir_p pep_pac_ci.ie_tipo_termo_cir%type, nr_sequencia_p bigint, ie_tipo_origem_p text) AS $body$
BEGIN

if ie_tipo_origem_p = 'A' then
	update 	pep_pac_ci
	set		nr_seq_agenda = nr_sequencia_p, ie_tipo_termo_cir = ie_tipo_termo_cir_p
	where 	pep_pac_ci.nr_sequencia = nr_seq_consentimento_p;

	commit;
elsif ie_tipo_origem_p = 'C' then
	update 	pep_pac_ci
	set		nr_cirurgia = nr_sequencia_p, ie_tipo_termo_cir = ie_tipo_termo_cir_p
	where 	pep_pac_ci.nr_sequencia = nr_seq_consentimento_p;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
