CREATE OR REPLACE PROCEDURE sib_excluir_exclusao_sib ( nr_seq_lote_sib_p bigint, nr_seq_interf_sib_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_exclusao_del_w		bigint;
nr_seq_segurado_w		bigint;


BEGIN

select	a.nr_sequencia,
	a.nr_seq_segurado
into STRICT	nr_seq_exclusao_del_w,
	nr_seq_segurado_w
from	pls_interf_sib	a
where	a.nr_sequencia = nr_seq_interf_sib_p
and	a.nr_seq_lote_sib = nr_seq_lote_sib_p;

/* insere os dados da alteração na tabela sib_log_exclusao que rigistra as alterações excluidas */

insert	into sib_log_exclusao(	nr_sequencia, nr_seq_lote, dt_atualizacao,
				nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
				nr_seq_segurado, nr_seq_motivo_exclusao, dt_registro,
				nm_usuario_registro)
			values (	nextval('sib_log_exclusao_seq'), nr_seq_lote_sib_p, clock_timestamp(),
				nm_usuario_p, clock_timestamp(), nm_usuario_p,
				nr_seq_segurado_w, null, clock_timestamp(),
				nm_usuario_p);

delete	from pls_interf_sib
where	nr_sequencia	= nr_seq_interf_sib_p
and	nr_seq_lote_sib	= nr_seq_lote_sib_p
and	ie_tipo_reg	= 7;

CALL sib_ordenar_segurados_envio(nr_seq_lote_sib_p,nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
