SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sib_ordenar_segurados_envio ( nr_seq_lote_sib_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_interf_sib_w		bigint;
ie_tipo_reg_w			integer;
ie_tipo_reenvio_w		smallint;
qt_registros_w			bigint;
qt_percorrida_w			bigint := 0;
nr_sequencia_w			bigint;
nr_seq_registro_sib_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		ie_tipo_reg,
		ie_tipo_reenvio
	from	pls_interf_sib
	where	nr_seq_lote_sib	= nr_seq_lote_sib_p
	order by	ie_tipo_reg desc;


BEGIN

update	pls_interf_sib
set	nr_sequencia	= nr_sequencia * -1
where	nr_seq_lote_sib	= nr_seq_lote_sib_p;

select	count(*)
into STRICT	qt_registros_w
from	pls_interf_sib
where	nr_seq_lote_sib	= nr_seq_lote_sib_p;

open C01;
loop
fetch C01 into
	nr_seq_interf_sib_w,
	ie_tipo_reg_w,
	ie_tipo_reenvio_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	--aaschlote 11/05/2011 - Buscar os beneficiários de origem da conferência do SIB...
	if (ie_tipo_reenvio_w IS NOT NULL AND ie_tipo_reenvio_w::text <> '') then
		if (ie_tipo_reenvio_w	in (1,2)) then
			begin
			select	max(nr_sequencia)
			into STRICT	nr_seq_registro_sib_w
			from	pls_retorno_sib
			where	nr_seq_lote_sib	= nr_seq_lote_sib_p
			and	nr_sequencia	= nr_seq_interf_sib_w;
			exception
			when others then
				nr_seq_registro_sib_w	:= null;
			end;
		end if;
	end if;

	nr_sequencia_w	:= qt_registros_w - qt_percorrida_w;

	update	pls_interf_sib
	set	nr_sequencia	= nr_sequencia_w
	where	nr_seq_lote_sib	= nr_seq_lote_sib_p
	and	nr_sequencia	= nr_seq_interf_sib_w;

	--Continuando, caso encontrar o beneficiário da conferência, ajustar a sua sequência
	if (nr_seq_registro_sib_w IS NOT NULL AND nr_seq_registro_sib_w::text <> '') then
		update	pls_retorno_sib
		set	nr_seq_lote_envio	= nr_seq_lote_sib_p,
			nr_seq_interf_sib	= nr_sequencia_w
		where	nr_sequencia		= nr_seq_registro_sib_w;
	end if;

	qt_percorrida_w	:= qt_percorrida_w + 1;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
