CREATE OR REPLACE PROCEDURE sinan_obter_dados_ocor_pf ( cd_paciente_p text, ds_bairro_p INOUT text, ds_municipio_p INOUT text, sg_estado_p INOUT text, cd_tipo_logradouro_p INOUT text) AS $body$
DECLARE


ds_bairro_w		varchar(80);
ds_municipio_w		varchar(40);
sg_estado_w		compl_pessoa_fisica.sg_estado%type;
cd_tipo_logradouro_w	varchar(3);
qt_linha_w		varchar(10);


BEGIN

if (cd_paciente_p IS NOT NULL AND cd_paciente_p::text <> '') then

        select 	count(*)
	into STRICT	qt_linha_w
	from 	compl_pessoa_fisica
	where	cd_pessoa_fisica = cd_paciente_p
	and	ie_tipo_complemento = 1;

	if (coalesce(qt_linha_w,'0') <> '0') then

		select	substr(ds_bairro,1,80),
			substr(ds_municipio,1,40),
			substr(sg_estado,1,2),
			substr(cd_tipo_logradouro,1,15)
		into STRICT	ds_bairro_w,
			ds_municipio_w,
			sg_estado_w,
			cd_tipo_logradouro_w
		from	compl_pessoa_fisica
		where	cd_pessoa_fisica = cd_paciente_p
		and	ie_tipo_complemento = 1;

		ds_bairro_p := ds_bairro_w;
		ds_municipio_p := ds_municipio_w;
		sg_estado_p := sg_estado_w;
		cd_tipo_logradouro_p := cd_tipo_logradouro_w;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
