CREATE OR REPLACE PROCEDURE sinc_hor_cpoe_material (cd_pessoa_fisica_p bigint, nr_seq_cpoe_p bigint, hr_prim_horario_p text, dt_inicio_medic_p timestamp, ds_horarios_p text, nr_ocorrencia_p bigint, nm_usuario_p text, ie_tipo_item_p text, nr_atendimento_p bigint) AS $body$
DECLARE


nr_prescricao_w				prescr_medica.nr_prescricao%type;


BEGIN

if (dt_inicio_medic_p IS NOT NULL AND dt_inicio_medic_p::text <> '') then

	nr_prescricao_w	:= obter_prescr_item_cpoe(nr_seq_cpoe_p,ie_tipo_item_p, nr_atendimento_p, cd_pessoa_fisica_p);

	if (ie_tipo_item_p in ('M', 'MAT')) then

		update prescr_material
		set hr_prim_horario = hr_prim_horario_p,
			dt_inicio_medic = dt_inicio_medic_p,
			ds_horarios = ds_horarios_p,
			nr_ocorrencia = nr_ocorrencia_p,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where nr_prescricao = nr_prescricao_w
		and	nr_seq_mat_cpoe = nr_seq_cpoe_p;

	elsif (ie_tipo_item_p = 'SOL') then

		update prescr_solucao
		set hr_prim_horario = hr_prim_horario_p,
			ds_horarios = ds_horarios_p,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where nr_prescricao = nr_prescricao_w
		and nr_seq_solucao = (SELECT max(nr_sequencia_solucao)
						    from prescr_material
							where nr_seq_mat_cpoe = nr_seq_cpoe_p
							and nr_prescricao = nr_prescricao_w);

	elsif (ie_tipo_item_p = 'P') then

		update prescr_procedimento
		set dt_prev_execucao = dt_inicio_medic_p,
			ds_horarios = ds_horarios_p,
			nr_ocorrencia = nr_ocorrencia_p,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where nr_prescricao = nr_prescricao_w
		and nr_seq_proc_cpoe = nr_seq_cpoe_p;

	elsif (ie_tipo_item_p = 'R') then

		update prescr_recomendacao
		set hr_prim_horario = hr_prim_horario_p,
			dt_prim_horario = dt_inicio_medic_p,
			ds_horarios = ds_horarios_p,
			nr_ocorrencia = nr_ocorrencia_p,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where nr_prescricao = nr_prescricao_w
		and	nr_seq_rec_cpoe = nr_seq_cpoe_p;

	end if;

	commit;

 end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
