SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sinopse_laudo ( nr_seq_laudo_p bigint, nm_usuario_p text, ds_orientacao_p text, ds_conclusao_p text, ds_tipo_p text) AS $body$
BEGIN
  if (ds_tipo_p = '0') then

    insert into laudo_paciente_sinopse(	nr_sequencia,
	   							        nr_seq_laudo,
										ds_conclusao,
										ds_orientacao,
										dt_atualizacao,
										dt_atualizacao_nrec,
										nm_usuario,
										nm_usuario_nrec
									  ) values (	nextval('laudo_paciente_sinopse_seq'),
										nr_seq_laudo_p,
										ds_conclusao_p,
										ds_orientacao_p,
										clock_timestamp(),
										clock_timestamp(),
										nm_usuario_p,
										nm_usuario_p
									  );
  elsif (ds_tipo_p = '1') then

        update	laudo_paciente_sinopse
		set     ds_conclusao = ds_conclusao_p,
			    ds_orientacao = ds_orientacao_p
		where   nr_seq_laudo = nr_seq_laudo_p;

  elsif (ds_tipo_p = '2') then

        delete from laudo_paciente_sinopse
		where   nr_seq_laudo = nr_seq_laudo_p;

  end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
