SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sip_atualizar_resumo_conta ( nr_seq_lote_sip_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_periodo_inicial_w		timestamp;
dt_periodo_final_w		timestamp;
ie_tipo_conta_w			varchar(1);
nr_seq_conta_w			bigint;
nr_seq_item_w			bigint;
cd_procedimento_w		bigint;
ie_origem_proced_w		bigint;
ie_tipo_guia_w			varchar(2);
nr_seq_tipo_atendimento_w	bigint;
cd_medico_executor_w		varchar(10);
ie_regime_internacao_w		varchar(1);
nr_seq_conselho_w		bigint;
nr_seq_grupo_ans_w		bigint;
ie_tipo_desp_mat_w		varchar(2);
cd_estabelecimento_w		bigint;

C01 CURSOR FOR
	SELECT	1,
		b.nr_sequencia,
		c.nr_sequencia,
		c.cd_procedimento,
		c.ie_origem_proced,
		b.cd_estabelecimento
	from	pls_conta_proc		c,
		pls_conta		b,
		pls_protocolo_conta	a
	where	a.nr_sequencia	= b.nr_seq_protocolo
	and	b.nr_sequencia	= c.nr_seq_conta
	and	a.ie_status	= 3
	and	a.dt_mes_competencia between dt_periodo_inicial_w and dt_periodo_final_w
	and	coalesce(b.ie_tipo_segurado,'B') in ('B','A')
	
union all

	SELECT	2,
		b.nr_sequencia,
		c.nr_sequencia,
		null,
		null,
		b.cd_estabelecimento
	from	pls_conta_mat		c,
		pls_conta		b,
		pls_protocolo_conta	a
	where	a.nr_sequencia	= b.nr_seq_protocolo
	and	b.nr_sequencia	= c.nr_seq_conta
	and	a.ie_status	= 3
	and	a.dt_mes_competencia between dt_periodo_inicial_w and dt_periodo_final_w
	and	coalesce(b.ie_tipo_segurado,'B') in ('B','A');


BEGIN

select	dt_periodo_inicial,
	coalesce(dt_periodo_final, clock_timestamp())
into STRICT	dt_periodo_inicial_w,
	dt_periodo_final_w
from	pls_lote_sip
where	nr_sequencia	= nr_seq_lote_sip_p;

open C01;
loop
fetch C01 into
	ie_tipo_conta_w,
	nr_seq_conta_w,
	nr_seq_item_w,
	cd_procedimento_w,
	ie_origem_proced_w,
	cd_estabelecimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	ie_tipo_guia,
		nr_seq_tipo_atendimento,
		cd_medico_executor,
		ie_regime_internacao
	into STRICT	ie_tipo_guia_w,
		nr_seq_tipo_atendimento_w,
		cd_medico_executor_w,
		ie_regime_internacao_w
	from	pls_conta a
	where	nr_sequencia	= nr_seq_conta_w;

	select	max(nr_seq_conselho)
	into STRICT	nr_seq_conselho_w
	from	pessoa_fisica
	where	cd_pessoa_fisica	= cd_medico_executor_w;

	if (ie_tipo_conta_w = '2') then
		select	coalesce(max(ie_tipo_despesa),'')
		into STRICT	ie_tipo_desp_mat_w
		from	pls_conta_mat
		where	nr_sequencia	= nr_seq_item_w;
	end if;

	select	pls_obter_grupo_ans(cd_procedimento_w, ie_origem_proced_w, nr_seq_conselho_w,
				nr_seq_tipo_atendimento_w, ie_tipo_guia_w, ie_regime_internacao_w,
				ie_tipo_desp_mat_w, 'G', cd_estabelecimento_w,
				nr_seq_conta_w)
	into STRICT	nr_seq_grupo_ans_w
	;

	delete	from sip_resumo_conta
	where	nr_seq_conta	= nr_seq_conta_w
	and	ie_tipo_conta	= ie_tipo_conta_w
	and	nr_seq_item	= nr_seq_item_w;

	CALL sip_gerar_resumo_conta(nr_seq_item_w, ie_tipo_conta_w, nm_usuario_p);
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
