CREATE OR REPLACE PROCEDURE sip_calcular_despesa_assist ( nr_seq_lote_sip_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
dt_periodo_inicial_w	timestamp;
dt_periodo_final_w	timestamp;
vl_despesa_w		double precision	:= 0;


BEGIN 
 
begin 
select	dt_periodo_inicial, 
	coalesce(dt_periodo_final, clock_timestamp()) 
into STRICT	dt_periodo_inicial_w, 
	dt_periodo_final_w 
from	pls_lote_sip 
where	nr_sequencia	= nr_seq_lote_sip_p;
exception 
	when others then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort( 266994, 'NR_SEQ_LOTE_SIP='||nr_seq_lote_sip_p);
end;
 
select	coalesce(sum(c.vl_movimento),0) 
into STRICT	vl_despesa_w 
from	ctb_mes_ref		d, 
	ctb_balancete_v		c, 
	sip_conta_despesa	b, 
	sip_item_despesa	a 
where	a.nr_sequencia		= b.nr_seq_item_despesa 
and	b.cd_conta_contabil	= c.cd_conta_contabil 
and	c.nr_seq_mes_ref	= d.nr_sequencia 
and	c.ie_normal_encerramento	= 'E' 
and	d.dt_referencia between dt_periodo_inicial_w and dt_periodo_final_w 
order by b.cd_conta_contabil;	
 
update	pls_lote_sip 
set	vl_despesas	= vl_despesa_w 
where	nr_sequencia	= nr_seq_lote_sip_p;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
