CREATE OR REPLACE PROCEDURE sip_duplicar_regra_lista ( nr_seq_regra_lista_p text, ie_tipo_regra_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_regra_lista_w	varchar(2000);
nr_seq_regra_w		bigint;


BEGIN

nr_seq_regra_lista_w	:= nr_seq_regra_lista_p;
while (nr_seq_regra_lista_w IS NOT NULL AND nr_seq_regra_lista_w::text <> '') loop
	begin
	nr_seq_regra_w		:= substr(nr_seq_regra_lista_w, 1, position(',' in nr_seq_regra_lista_w) - 1);
	nr_seq_regra_lista_w	:= substr(nr_seq_regra_lista_w, position(',' in nr_seq_regra_lista_w) + 1, length(nr_seq_regra_lista_w));

	if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then
		CALL sip_duplicar_regra(
			nr_seq_regra_w,
			ie_tipo_regra_p,
			nm_usuario_p);
	end if;
	end;
end loop;

end;
$body$
