CREATE OR REPLACE PROCEDURE sip_gerar_grupo_beneficiario ( nr_seq_lote_sip_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_periodo_inicial_w	timestamp;
dt_periodo_final_w	timestamp;
vl_dominio_w		varchar(15);
qt_beneficiarios_w	bigint	:= 0;

C01 CURSOR FOR
	SELECT	vl_dominio
	from 	valor_dominio
	where 	cd_dominio = 1923
	order by vl_dominio;


BEGIN

begin
select	dt_periodo_inicial,
	coalesce(dt_periodo_final, clock_timestamp())
into STRICT	dt_periodo_inicial_w,
	dt_periodo_final_w
from	pls_lote_sip
where	nr_sequencia	= nr_seq_lote_sip_p;
exception
	when others then
	CALL wheb_mensagem_pck.exibir_mensagem_abort('Problema na leitura dos dados do lote SIP (' || nr_seq_lote_sip_p || ')');
end;

/*delete from logxxxx_tasy
where	cd_log = 2009;*/
open C01;
loop
fetch C01 into
	vl_dominio_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	begin
	select	count(*)
	into STRICT	qt_beneficiarios_w
	from	pls_contrato	b,
		pls_segurado	a
	where	a.nr_seq_contrato	= b.nr_sequencia
	and	a.dt_inclusao_operadora	<= dt_periodo_final_w
	and	coalesce(a.dt_rescisao::text, '') = ''
	and	b.ie_tipo_beneficiario 	= vl_dominio_w
	group by b.ie_tipo_beneficiario;
	exception
		when others then
		qt_beneficiarios_w	:= 0;
	end;


	CALL sip_gravar_item_despesa(null, null, null,
				null, null, 0,
				0, 0, 0,
				nr_seq_lote_sip_p, null, null,
				vl_dominio_w, null, null,
				null, null, null,
				qt_beneficiarios_w, '', nm_usuario_p,
				0, 0, 0);

	/*insert into logxxxxx_tasy
		(dt_atualizacao,
		nm_usuario,
		cd_log,
		ds_log)
	values (sysdate,
		nm_usuario_p,
		2009,
		vl_dominio_w); */
	commit;
	CALL gravar_processo_longo('Cursor C01','SIP_GERAR_GRUPO_BENEFICIARIO',-1);
	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
