SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE siscolo_liberar_laudo ( nr_sequencia_p bigint , nm_usuario_p text ) AS $body$
DECLARE


dt_liberacao_w		timestamp;
qt_inco_laudo_w		smallint	:= 0;


BEGIN

select	count(*)
into STRICT	qt_inco_laudo_w
from	sus_inconsistencia_laudo	b,
	sus_inco_reg_laudo		a
where	a.nr_seq_inconsistencia	= b.nr_sequencia
and	a.nr_seq_laudo		= nr_sequencia_p
and	b.ie_permite_liberar	= 'N';

if (qt_inco_laudo_w	> 0) then
	CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(1088687);
end if;

begin
select 	dt_liberacao
into STRICT	dt_liberacao_w
from	siscolo_atendimento
where 	nr_sequencia = nr_sequencia_p;
exception
	when others then
	dt_liberacao_w := null;
end;

if (coalesce(dt_liberacao_w::text, '') = '') then
	begin
	update 	siscolo_atendimento
	set	dt_liberacao 	= clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia 	= nr_sequencia_p;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
