CREATE OR REPLACE PROCEDURE sismama_extorna_laudo_paciente ( nr_seq_sismama_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_laudo_w		bigint := 0;
nr_atendimento_w	bigint;
cd_profissional_w	varchar(10);
dt_emissao_w		timestamp;
nr_seq_proc_w		bigint;



BEGIN

select 	cd_profissional,
	nr_atendimento,
	dt_emissao,
	coalesce(nr_seq_proc_pac,0)
into STRICT	cd_profissional_w,
	nr_atendimento_w,
	dt_emissao_w,
	nr_seq_proc_w
from 	sismama_atendimento
where 	nr_sequencia = nr_seq_sismama_p;

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_laudo_w
from	laudo_paciente
where	nr_atendimento 	= nr_atendimento_w
and	cd_medico_resp	= cd_profissional_w
and	dt_liberacao	= dt_emissao_w;

if (nr_seq_proc_w <> 0) then
	begin
	update  sismama_atendimento
	set		nr_seq_proc_pac  = NULL
	where	nr_sequencia = nr_seq_sismama_p;
	end;
end if;

if (nr_seq_laudo_w <> 0) then
	begin

	delete 	from laudo_paciente
	where	nr_sequencia = nr_seq_laudo_w;

	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
