SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE sismama_gerar_liberacao_laudo (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_liberacao_w		timestamp;
qt_inco_laudo_w		smallint	:= 0;
ie_libera_conclusao_w	varchar(15)	:= 'N';


BEGIN

ie_libera_conclusao_w	:= coalesce(obter_valor_param_usuario(1125,153,obter_perfil_ativo,nm_usuario_p,coalesce(wheb_usuario_pck.get_cd_estabelecimento,0)),'N');

select	count(*)
into STRICT	qt_inco_laudo_w
from	sus_inconsistencia_laudo	b,
	sus_inco_reg_laudo		a
where	a.nr_seq_inconsistencia	= b.nr_sequencia
and	a.nr_seq_laudo		= nr_sequencia_p
and	b.ie_permite_liberar	= 'N';

if (qt_inco_laudo_w	> 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(249231);/*Laudo com inconsistências, não é possível liberá-lo.*/
end if;

begin
select 	dt_liberacao
into STRICT	dt_liberacao_w
from	sismama_atendimento
where 	nr_sequencia = nr_sequencia_p;
exception
	when others then
	dt_liberacao_w := null;
end;

if (coalesce(dt_liberacao_w::text, '') = '') then
	begin

	update 	sismama_atendimento
	set	dt_liberacao 	= clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_p;

	if (ie_libera_conclusao_w = 'S') then
		begin

		update 	sismama_mam_conclusao
		set	dt_liberacao 	= clock_timestamp(),
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_seq_sismama	= nr_sequencia_p;

		end;
	end if;

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE sismama_gerar_liberacao_laudo (nr_sequencia_p bigint, nm_usuario_p text) FROM PUBLIC;

