CREATE OR REPLACE PROCEDURE slot_overlap_services (nr_minuto_duracao_p bigint, dt_agenda_p timestamp, cd_agenda_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, nr_seq_agenda_p bigint, ds_exist_patient_detail_p INOUT text) AS $body$
DECLARE


nr_minuto_duracao_w		bigint;				
hr_param_w			timestamp;
qt_agendado_w			bigint;
ie_sobreposicao_encaixe_w	varchar(1) := 'S';
nm_pessoa_fisica_w 		varchar(255);
dt_agenda_w			varchar(255);


c01 CURSOR FOR
	SELECT	obter_nome_pf(a.cd_pessoa_fisica),
		date_as_varchar2(a.dt_agenda, 'shortTime')
	from	agenda_consulta a
	where	cd_agenda = cd_agenda_p
	and    dt_agenda > dt_agenda_p and  dt_agenda <= hr_param_w - 1/1440
	and	((ie_sobreposicao_encaixe_w = 'N' and coalesce(ie_encaixe, 'N') <> 'S' ) or ie_sobreposicao_encaixe_w = 'S')
	and (ie_status_agenda not in ('C','L','B','F','I','II'))
	and 	nr_sequencia <> nr_seq_agenda_p
	order by 1;


BEGIN

select	coalesce(max(ie_sobreposicao_encaixe),'S')
into STRICT	ie_sobreposicao_encaixe_w
from	parametro_agenda
where	cd_estabelecimento = cd_estabelecimento_p;

hr_param_w := dt_agenda_p + nr_minuto_duracao_p / 1440;

select	count(*)
into STRICT	qt_agendado_w
from	agenda_consulta
where	cd_agenda = cd_agenda_p
and    dt_agenda > dt_agenda_p and  dt_agenda <= hr_param_w - 1/1440
and	((ie_sobreposicao_encaixe_w = 'N' and coalesce(ie_encaixe, 'N') <> 'S' ) or ie_sobreposicao_encaixe_w = 'S')
and (ie_status_agenda not in ('C','L','B','F','I','II'))
AND 	nr_sequencia <> nr_seq_agenda_p;

if (qt_agendado_w > 0) then
        open C01;
	loop
	fetch C01 into
		nm_pessoa_fisica_w,
		dt_agenda_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		
		if (nm_pessoa_fisica_w IS NOT NULL AND nm_pessoa_fisica_w::text <> '') then
			ds_exist_patient_detail_p := nm_pessoa_fisica_w || ' - ' || dt_agenda_w || chr(10) || ds_exist_patient_detail_p;
		end if;
		
		end;
	end loop;
        close C01;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
