CREATE OR REPLACE PROCEDURE sl_gerar_check_list ( nr_seq_check_list_p bigint, cd_perfil_p bigint, nm_usuario_p text, ie_status_serv_p text default 'F') AS $body$
DECLARE


nr_seq_item_w	bigint;
ie_resultado_w	varchar(1);

C01 CURSOR FOR
	SELECT	b.nr_sequencia
	from	sl_grupo_check_list a,
		sl_item_check_list b
	where 	coalesce(a.ie_situacao, 'A') = 'A'
	and 	coalesce(b.ie_situacao, 'A') = 'A'
	and	coalesce(cd_perfil,cd_perfil_p) = cd_perfil_p
	and	b.nr_seq_grupo_check_list = a.nr_sequencia
	and (coalesce(a.ie_aplicacao_checklist,'F') = 'A'
	or	exists (SELECT 1
			from sl_grupo_check_list y
			where y.nr_sequencia = a.nr_sequencia
			and coalesce(y.ie_aplicacao_checklist,'F') = CASE WHEN ie_status_serv_p='P' THEN  'I'  ELSE 'F' END ));


BEGIN
ie_resultado_w := Obter_param_Usuario(75, 75, obter_perfil_ativo, nm_usuario_p, obter_estabelecimento_ativo, ie_resultado_w);
open C01;
loop
fetch C01 into
	nr_seq_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	insert into sl_check_list_unid_item(nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_checklist,
			nr_seq_item_check_list,
			ie_resultado,
			ie_nao_aplica)
		values (
			nextval('sl_check_list_unid_item_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_check_list_p,
			nr_seq_item_w,
			ie_resultado_w,
			'N');

	end;
end loop;
close C01;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
