CREATE OR REPLACE PROCEDURE solicitar_perda ( nr_seq_ordem_prod_p bigint, cd_material_p bigint, nr_seq_paciente_p bigint) AS $body$
BEGIN

update	paciente_atendimento
set	dt_recebimento_medic  = NULL
where	nr_seq_paciente = nr_seq_paciente_p;

update	can_ordem_prod
set	dt_solic_perda	= clock_timestamp()
where	nr_sequencia	= nr_seq_ordem_prod_p;

update	can_ordem_item_prescr
set	dt_solic_perda	= clock_timestamp()
where	nr_seq_ordem	= nr_seq_ordem_prod_p
and	cd_material	= cd_material_p;

update	can_ordem_prod_mat
set	dt_solic_perda	= clock_timestamp()
where	nr_seq_ordem 	= nr_seq_ordem_prod_p
and	cd_material	= cd_material_p;

commit;

end;
