SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE solicitar_prontuario_agendas (cd_pessoa_fisica_p text, nm_usuario_p text) AS $body$
DECLARE


cd_agenda_w		bigint;
nr_seq_agenda_w	agenda_consulta.nr_sequencia%type;
dt_agenda_w		timestamp;

/* obter agendas */

c01 CURSOR FOR
SELECT	a.cd_agenda,
	b.nr_sequencia,
	b.dt_agenda
from	agenda a,
	agenda_consulta b
where	a.cd_agenda = b.cd_agenda
and	a.ie_situacao = 'A'
and	a.ie_gerar_solic_pront = 'S'
and	a.cd_tipo_agenda = 5
and	b.ie_status_agenda = 'N'
and	b.dt_agenda >= clock_timestamp()
and	b.cd_pessoa_fisica = cd_pessoa_fisica_p
order by
	a.cd_agenda,
	b.nr_sequencia;


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	open c01;
	loop
	fetch c01 into	cd_agenda_w,
				nr_seq_agenda_w,
				dt_agenda_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		CALL gerar_solic_pront_agenda(cd_pessoa_fisica_p, nr_seq_agenda_w, cd_agenda_w, dt_agenda_w, nm_usuario_p);
		end;
	end loop;
	close c01;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE solicitar_prontuario_agendas (cd_pessoa_fisica_p text, nm_usuario_p text) FROM PUBLIC;

