CREATE OR REPLACE PROCEDURE solic_comp_alt_grupo_comp ( nr_solic_compra_p bigint, nr_seq_grupo_p bigint, nr_seq_grupo_novo_p bigint, cd_comprador_p text, cd_comprador_novo_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ds_historico_w		varchar(2000);


BEGIN 
 
if (nr_solic_compra_p > 0) then 
	begin 
	 
	update	solic_compra 
	set	nr_seq_grupo_resp_compra = nr_seq_grupo_novo_p, 
		cd_comprador_resp = cd_comprador_novo_p 
	where	nr_solic_compra = nr_solic_compra_p;
	 
	/* 
	Alterado grupo de compras através da opção Alterar grupo de compras. 
	Antes: 
	Grupo: #@NR_SEQ_GRUPO#@ - #@DS_GRUPO_COMPRA#@ 
	Comprador: #@CD_COMPRADOR#@ - #@NM_COMPRADOR#@ 
	Depois: 
	Grupo: #@NR_SEQ_GRUPO_NOVO#@ - #@DS_GRUPO_NOVO#@ 
	Comprador: #@CD_COMPRADOR_NOVO#@ - #@NM_COMPRADOR_NOVO#@ 
	*/
 
 
	ds_historico_w	:= substr( wheb_mensagem_pck.get_texto(306854, 
				'NR_SEQ_GRUPO=' || nr_seq_grupo_p || 
				';DS_GRUPO_COMPRA=' || obter_nome_grupo_compras(nr_seq_grupo_p,cd_estabelecimento_p) || 
				';CD_COMPRADOR=' || cd_comprador_p || 
				';NM_COMPRADOR=' || sup_obter_nome_comprador(cd_estabelecimento_p, cd_comprador_p) || 
				';NR_SEQ_GRUPO_NOVO=' || nr_seq_grupo_novo_p || 
				';DS_GRUPO_NOVO=' || obter_nome_grupo_compras(nr_seq_grupo_novo_p,cd_estabelecimento_p) || 
				';CD_COMPRADOR_NOVO=' || cd_comprador_novo_p || 
				';NM_COMPRADOR_NOVO=' || sup_obter_nome_comprador(cd_estabelecimento_p, cd_comprador_novo_p)),1,2000);
	CALL gerar_historico_solic_compra(	nr_solic_compra_p, 
					wheb_mensagem_pck.get_texto(306861), 
					ds_historico_w, 
					'AGC', 
					nm_usuario_p);
	end;
end if;
 
commit;
 
end;
$body$
