SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE spa_atualizar_dados_movimento (nr_seq_movimento_p bigint) AS $body$
DECLARE

 
cd_classificacao_w	integer;
nr_interno_conta_w	bigint;
nr_atendimento_w	bigint;
nr_seq_nf_w		bigint;
					

BEGIN 
 
if (nr_seq_movimento_p IS NOT NULL AND nr_seq_movimento_p::text <> '') then 
 
	select	max(cd_classificacao), 
		max(nr_interno_conta), 
		max(nr_seq_nf) 
	into STRICT	cd_classificacao_w, 
		nr_interno_conta_w, 
		nr_seq_nf_w 
	from	spa_movimento 
	where	nr_sequencia = nr_seq_movimento_p;
	 
	if (cd_classificacao_w = 3) then 
	 
		select	coalesce(max(nr_interno_conta),0) 
		into STRICT	nr_interno_conta_w 
		from	nota_fiscal 
		where	nr_sequencia = nr_seq_nf_w;
		 
	end if;
	 
	if (nr_interno_conta_w <> 0) then	 
		select	max(nr_atendimento) 
		into STRICT	nr_atendimento_w 
		from	conta_paciente 
		where	nr_interno_conta = nr_interno_conta_w;		
	else		 
		nr_interno_conta_w	:= null;
		nr_atendimento_w	:= null;
	end if;
	 
	update	spa_movimento 
	set	nr_atendimento = nr_atendimento_w, 
		nr_interno_conta = nr_interno_conta_w 
	where	nr_sequencia = nr_seq_movimento_p;
	 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
