CREATE OR REPLACE PROCEDURE spa_cancelar_parecer ( nr_seq_spa_p spa_parecer.nr_seq_spa%type, nm_usuario_p usuario.nm_usuario%type, nr_seq_parecer_p spa_parecer.nr_sequencia%type default null) AS $body$
DECLARE


nr_sequencia_w		spa_parecer.nr_sequencia%type;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	SPA_PARECER
where	coalesce(DS_RESPOSTA_PARECER::text, '') = ''
and	nr_seq_spa	    = NR_SEQ_SPA_P
and 	nr_sequencia 	    = coalesce(nr_seq_parecer_p,nr_sequencia);

update	spa_parecer
set	dt_cancelamento		= clock_timestamp(),
	dt_atualizacao		= clock_timestamp()
where	nr_seq_spa = nr_seq_spa_p
and	nr_sequencia = nr_sequencia_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
