SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE spa_desfazer_aprovacao ( nr_seq_spa_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_rota_aprov_w		varchar(1);
ie_status_w		spa.ie_status%type;


BEGIN


if (coalesce(nr_seq_spa_p,0) <> 0) then

	select	ie_status
	into STRICT 	ie_status_w
	from 	spa
	where nr_sequencia = nr_seq_spa_p;

	delete	from spa_aprovacao
	where	nr_seq_spa = nr_seq_spa_p;

	update	spa
	set	ie_status = 1
	where	nr_sequencia = nr_seq_spa_p;

	update	spa_hist_email a
	set	dt_estorno_lib_spa 	= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		dt_atualizacao  	= clock_timestamp()
	where	a.nr_seq_spa		= nr_seq_spa_p
	and	coalesce(dt_estorno_lib_spa::text, '') = '';

	--spa_liberar(nr_seq_spa_p, nm_usuario_p, ie_rota_aprov_w);
	if (coalesce(ie_rota_aprov_w,'S') = 'N') then
		update	spa
		set	ie_status = 4
		where	nr_sequencia = nr_seq_spa_p;
	end if;

	CALL spa_gerar_historico(nr_seq_spa_p,nm_usuario_p,ie_status_w,1,7,'','N');
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE spa_desfazer_aprovacao ( nr_seq_spa_p bigint, nm_usuario_p text) FROM PUBLIC;

