CREATE OR REPLACE PROCEDURE spa_gerar_historico ( nr_seq_spa_p bigint, nm_usuario_p text, ie_status_ant_p bigint, ie_status_atual_p bigint, ie_tipo_historico_p bigint, ds_parecer_p text, ie_commit_p text) AS $body$
DECLARE


nr_sequencia_w			spa_historico.nr_sequencia%type;


BEGIN
/* dominio 4641 - status da spa
1               Cadastro da SPA
2               Liberada
3               Em aprovação
4               Aprovada
5               Executada
6               Cancelada   */
/* dominio 8376 - tipo de historico
1               Aprovação
2               Liberação
3               Cancelamento
4               Execução
5               Solicitação parecer
6               Resposta Parecer
 7              Desfazer aprovação
 8              Desfazer liberação
*/
if (coalesce(nr_seq_spa_p,0) > 0) then

	select	nextval('spa_historico_seq')
	into STRICT	nr_sequencia_w
	;

	insert into spa_historico(	nr_sequencia,
					nr_seq_spa,
					nm_usuario,
					dt_atualizacao,
					ie_status_ant,
					ie_status_atual,
					ie_tipo_historico,
					ds_parecer)
	values (	nr_sequencia_w,
					nr_seq_spa_p,
					nm_usuario_p,
					clock_timestamp(),
					ie_status_ant_p,
					ie_status_atual_p,
					ie_tipo_historico_p,
					ds_parecer_p);

	if (coalesce(ie_commit_p,'N') = 'S') then
		commit;
	end if;
end if;
end;
$body$
