SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE spa_gerar_movimento_titulo ( nr_seq_spa_p bigint, nr_titulo_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_atendimento_w	bigint;
nr_interno_conta_w	bigint;
ie_data_contabil_spa_w	varchar(1);
dt_spa_w		spa.dt_spa%type;


BEGIN


ie_data_contabil_spa_w  := coalesce(Obter_valor_param_usuario(7053, 23, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento),'N');
dt_spa_w		:=null;

if (ie_data_contabil_spa_w = 'S') then
	begin
		select	dt_spa
		into STRICT	dt_spa_w
		from	spa
		where	nr_sequencia = nr_seq_spa_p;
	exception
	when others then
		dt_spa_w:=null;
	end;
end if;


if (coalesce(nr_titulo_p,0) <> 0) then

	select	max(nr_atendimento),
		max(nr_interno_conta)
	into STRICT	nr_atendimento_w,
		nr_interno_conta_w
	from	titulo_receber
	where	nr_titulo = nr_titulo_p;

	insert into spa_movimento(
		nr_sequencia,
		nr_seq_spa,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_classificacao,
		nr_atendimento,
		nr_interno_conta,
		nr_titulo,
		dt_contabil
	) values (
		nextval('spa_movimento_seq'),
		nr_seq_spa_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		4,
		nr_atendimento_w,
		nr_interno_conta_w,
		nr_titulo_p,
		dt_spa_w
	);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
