CREATE OR REPLACE PROCEDURE spa_inserir_analise_rec_glosa (nr_seq_spa_p bigint, nr_seq_analise_p bigint, ie_commit_p text, nm_usuario_p text) AS $body$
DECLARE

	 
nr_interno_conta_w 	conta_paciente.nr_interno_conta%type;
nr_atendimento_w 	atendimento_paciente.nr_atendimento%type;
nr_seq_movimento_w	spa_movimento.nr_sequencia%type;
	
C01 CURSOR FOR 
	SELECT 	nr_interno_conta 
	from 	LOTE_AUDIT_HIST_GUIA 
	where 	NR_SEQ_LOTE_HIST = nr_seq_analise_p;

BEGIN
 
if (coalesce(nr_seq_analise_p,0) > 0) and (coalesce(nr_seq_spa_p,0) > 0) then 
	open C01;
	loop 
	fetch C01 into	 
		nr_interno_conta_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		select 	nextval('spa_movimento_seq') 
		into STRICT	nr_seq_movimento_w 
		;
		 
		insert into spa_movimento(nr_sequencia, 
				nr_seq_spa, 
				nm_usuario, 
				nm_usuario_nrec, 
				dt_atualizacao, 
				dt_atualizacao_nrec, 
				cd_classificacao, 
				nr_interno_conta, 
				nr_atendimento) 
		values (nr_seq_movimento_w, 
				nr_seq_spa_p, 
				nm_usuario_p, 
				nm_usuario_p, 
				clock_timestamp(), 
				clock_timestamp(), 
				2, 
				nr_interno_conta_w, 
				Obter_Atendimento_Conta(nr_interno_conta_w) 
				);
		end;
	end loop;
	close C01;
end if;
 
if (ie_commit_p = 'S') then 
	commit;
end if;
 
end;
