CREATE OR REPLACE PROCEDURE spa_obter_pessoa_substituta ( cd_cargo_p bigint, nm_usuario_p text, cd_solicitante_p text, dt_limite_p timestamp, ie_substituto_p INOUT text, cd_pessoa_substituta_p INOUT text, cd_estab_p bigint default null, nr_seq_tipo_spa_p bigint default null, nr_seq_motivo_spa_p bigint default null) AS $body$
DECLARE


qt_substituto_w		bigint;
ie_substituto_w		varchar(1);
cd_pessoa_substituta_w	varchar(10);
cd_pessoa_usuario_w	varchar(10);


BEGIN

ie_substituto_p		:= 'N';
cd_pessoa_substituta_p	:= null;

if (coalesce(cd_cargo_p,0) <> 0) then

	select	count(*)
	into STRICT	qt_substituto_w
	from	spa_substituto
	where	cd_cargo = cd_cargo_p
	and	trunc(dt_limite,'dd') >= trunc(dt_limite_p,'dd');

	if (coalesce(qt_substituto_w,0) > 0) then
		select	max(cd_pessoa_substituta)
		into STRICT	cd_pessoa_substituta_w
		from	spa_substituto
		where	cd_cargo = cd_cargo_p
		and	trunc(dt_limite,'dd') >= trunc(dt_limite_p,'dd')
		and (coalesce(cd_estab_p::text, '') = '' 		or coalesce(cd_estabelecimento,coalesce(cd_estab_p,0)) 	= coalesce(cd_estab_p,0))
		and (coalesce(nr_seq_tipo_spa_p::text, '') = '' 	or coalesce(nr_seq_tipo,coalesce(nr_seq_tipo_spa_p,0)) 	= coalesce(nr_seq_tipo_spa_p,0))
		and (coalesce(nr_seq_motivo_spa_p::text, '') = '' 	or coalesce(nr_seq_motivo,coalesce(nr_seq_motivo_spa_p,0))= coalesce(nr_seq_motivo_spa_p,0));
	end if;

end if;


if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	select	max(cd_pessoa_fisica)
	into STRICT	cd_pessoa_usuario_w
	from	usuario
	where	nm_usuario = nm_usuario_p;

	select	count(*)
	into STRICT	qt_substituto_w
	from	spa_substituto
	where	cd_pessoa_fisica = cd_pessoa_usuario_w
	and	trunc(dt_limite,'dd') >= trunc(dt_limite_p,'dd')
	and (coalesce(cd_estab_p::text, '') = '' 		or coalesce(cd_estabelecimento,coalesce(cd_estab_p,0)) 	 = coalesce(cd_estab_p,0))
	and (coalesce(nr_seq_tipo_spa_p::text, '') = '' 	or coalesce(nr_seq_tipo,coalesce(nr_seq_tipo_spa_p,0)) 	 = coalesce(nr_seq_tipo_spa_p,0))
	and (coalesce(nr_seq_motivo_spa_p::text, '') = '' 	or coalesce(nr_seq_motivo,coalesce(nr_seq_motivo_spa_p,0)) = coalesce(nr_seq_motivo_spa_p,0));

	if (coalesce(qt_substituto_w,0) > 0) then
		select	max(cd_pessoa_substituta)
		into STRICT	cd_pessoa_substituta_w
		from	spa_substituto
		where	cd_pessoa_fisica = cd_pessoa_usuario_w
		and	trunc(dt_limite,'dd') >= trunc(dt_limite_p,'dd')
		and (coalesce(cd_estab_p::text, '') = '' 		or coalesce(cd_estabelecimento,coalesce(cd_estab_p,0)) 	= coalesce(cd_estab_p,0))
		and (coalesce(nr_seq_tipo_spa_p::text, '') = '' 	or coalesce(nr_seq_tipo,coalesce(nr_seq_tipo_spa_p,0)) 	= coalesce(nr_seq_tipo_spa_p,0))
		and (coalesce(nr_seq_motivo_spa_p::text, '') = '' 	or coalesce(nr_seq_motivo,coalesce(nr_seq_motivo_spa_p,0))= coalesce(nr_seq_motivo_spa_p,0));
	end if;
end if;

if (cd_pessoa_substituta_w IS NOT NULL AND cd_pessoa_substituta_w::text <> '') then
	ie_substituto_w	:= 'S';
end if;

ie_substituto_p		:= ie_substituto_w;
cd_pessoa_substituta_p	:= cd_pessoa_substituta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
