CREATE OR REPLACE PROCEDURE spa_responder_parecer ( NR_SEQ_SPA_P bigint, DS_RESPONDER_PARECER_P text, nm_usuario_p text, nr_seq_parecer_p bigint default null) AS $body$
DECLARE


nr_sequencia_w		bigint;
ie_status_w		spa.ie_status%type;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	SPA_PARECER
where	coalesce(DS_RESPOSTA_PARECER::text, '') = ''
and	nr_seq_spa	    = NR_SEQ_SPA_P
and 	nr_sequencia 	    = coalesce(nr_seq_parecer_p,nr_sequencia);

update	SPA_PARECER
set	DT_RESP_PARECER		= clock_timestamp(),
	DS_RESPOSTA_PARECER	= substr(DS_RESPONDER_PARECER_P,1,254),
	DT_ATUALIZACAO		= clock_timestamp()
where	NR_SEQ_SPA = NR_SEQ_SPA_P
and	nr_sequencia = nr_sequencia_w;

select 	ie_status
into STRICT 	ie_status_w
from 	spa
where 	nr_sequencia = nr_seq_spa_p;

CALL spa_gerar_historico(nr_seq_spa_p,nm_usuario_p,ie_status_w,ie_status_w,6,substr(ds_responder_parecer_p,1,255),'N');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
