CREATE OR REPLACE PROCEDURE spa_solicitar_parecer (nr_seq_spa_p bigint, nm_usuario_parecer_p text, ds_solic_parecer_p text, nm_usuario_p text, ie_enviar_email_p text default 'N') AS $body$
DECLARE


ie_status_w	spa.ie_status%type;
ds_remetente_w	usuario.ds_email%type;
ds_destinatario_w	usuario.ds_email%type;


BEGIN

insert into spa_parecer(
	nr_sequencia,
	nr_seq_spa,
	nm_usuario_solic_parecer,
	nm_usuario_parecer,
	ds_pedido_parecer,
	ds_resposta_parecer,
	dt_resp_parecer,
	dt_solic_parecer,
	dt_atualizacao,
	nm_usuario)
values (nextval('spa_parecer_seq'),
	 nr_seq_spa_p,
	 nm_usuario_p,
	 nm_usuario_parecer_p,
	 ds_solic_parecer_p,
	 null,
	 null,
	 clock_timestamp(),
	 clock_timestamp(),
	 nm_usuario_p);

select 	ie_status
into STRICT 	ie_status_w
from 	spa
where 	nr_sequencia = nr_seq_spa_p;

CALL spa_gerar_historico(nr_seq_spa_p,nm_usuario_p,ie_status_w,ie_status_w,5,substr(ds_solic_parecer_p,1,255),'N');

if (coalesce(ie_enviar_email_p,'N') = 'S') then
	begin

	select	ds_email
	into STRICT	ds_remetente_w
	from	usuario
	where	nm_usuario = nm_usuario_p;

	select	ds_email
	into STRICT	ds_destinatario_w
	from	usuario
	where	nm_usuario = nm_usuario_parecer_p;

	--SPA - Solicitação de Parecer
	CALL enviar_email(wheb_mensagem_pck.get_texto(838218),substr(ds_solic_parecer_p,1,4000), ds_remetente_w, ds_destinatario_w, nm_usuario_p, 'B');
	end;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
