CREATE OR REPLACE PROCEDURE sp_inserir_exame_lst_medica (PNR_PRESCRICAO bigint, PNR_SEQ_PRESCRICAO bigint, PNR_SEQ_PROC_INTERNO bigint) AS $body$
BEGIN
  IF (PNR_PRESCRICAO IS NOT NULL AND PNR_PRESCRICAO::text <> '') AND (PNR_SEQ_PRESCRICAO IS NOT NULL AND PNR_SEQ_PRESCRICAO::text <> '') AND (PNR_SEQ_PROC_INTERNO IS NOT NULL AND PNR_SEQ_PROC_INTERNO::text <> '')AND (WHEB_USUARIO_PCK.GET_NM_USUARIO IS NOT NULL AND WHEB_USUARIO_PCK.GET_NM_USUARIO::text <> '')THEN
    INSERT INTO LISTA_EQUIPE_MED_EXEC(DT_ATUALIZACAO,
                                       NM_USUARIO, 
                                       DT_ATUALIZACAO_NREC, 
                                       NM_USUARIO_NREC, 
                                       NR_PRESCRICAO, 
                                       NR_SEQ_PRESCRICAO, 
                                       NR_SEQ_LISTA)
                                SELECT clock_timestamp(), 
                                       WHEB_USUARIO_PCK.GET_NM_USUARIO,
                                       clock_timestamp(),
                                       WHEB_USUARIO_PCK.GET_NM_USUARIO,
                                       PNR_PRESCRICAO,
                                       PNR_SEQ_PRESCRICAO,
                                       A.NR_SEQUENCIA
                                   FROM LISTA_EQUIPE_MEDICA A
                                   JOIN LISTA_EQUIPE_MED_ESPE B ON B.NR_SEQ_LISTA_MEDICA = A.NR_SEQUENCIA
                                   WHERE B.NR_SEQ_PROC_INTERNO = PNR_SEQ_PROC_INTERNO
                                   AND NOT EXISTS (SELECT 1 FROM LISTA_EQUIPE_MED_EXEC C WHERE C.NR_PRESCRICAO = PNR_PRESCRICAO
                                                    AND C.NR_SEQ_PRESCRICAO = PNR_SEQ_PRESCRICAO AND C.NR_SEQ_LISTA = A.NR_SEQUENCIA);
  END IF;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
