CREATE OR REPLACE PROCEDURE start_finish_validation_plan ( nr_seq_validation_plano_p bigint, ie_opcao_p text, nm_usuario_p text, ie_resultado_p text default null, ds_resultado_p text default null) AS $body$
DECLARE


/*
	IE_OPCAO

	F - Encerrar o plano
	S - Desfazer o encerramento
*/
BEGIN

if (ie_opcao_p = 'F') then

	update	reg_validation_control
	set	dt_fim_plano = clock_timestamp(),
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		ie_resultado = ie_resultado_p,
		ds_resultado = ds_resultado_p
	where	nr_sequencia = nr_seq_validation_plano_p;

elsif (ie_opcao_p = 'S') then

	update	reg_validation_control
	set	dt_fim_plano  = NULL,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_seq_validation_plano_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
