CREATE OR REPLACE PROCEDURE start_fleury_adep (nr_prescricao_p bigint, nr_seq_item_p bigint, nr_atendimento_p bigint, nr_prescricoes_p text, cd_item_p bigint, qt_item_p bigint, nr_seq_proc_interno_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, cd_pessoa_fisica_p bigint, cd_setor_atendimento_p bigint, cd_perfil_p bigint) AS $body$
DECLARE


ie_desagrupa_proced_w	varchar(1);
nr_prescricao_w         prescr_procedimento.nr_prescricao%type;
nr_seq_prescr_w         prescr_procedimento.nr_sequencia%type;

c01 CURSOR FOR
SELECT
    b.nr_prescricao,
    b.nr_sequencia
from prescr_procedimento b,
     prescr_medica a
where a.nr_prescricao = b.nr_prescricao and
    (((b.nr_sequencia	= nr_seq_item_p) and (b.nr_prescricao	= nr_prescricao_p) and (ie_desagrupa_proced_w = 'S')) or (ie_desagrupa_proced_w  = 'N'))
and	a.nr_atendimento = nr_atendimento_p
and	obter_se_contido(b.nr_prescricao, '(' || nr_prescricoes_p || ')') = 'S'
and	b.cd_procedimento = cd_item_p
and	coalesce(b.qt_procedimento,1) = coalesce(qt_item_p,coalesce(b.qt_procedimento,1))
and coalesce(b.nr_seq_proc_interno, 1) = coalesce(nr_seq_proc_interno_p, coalesce(b.nr_seq_proc_interno,1));


BEGIN

    ie_desagrupa_proced_w := obter_param_usuario(1113, 463, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_desagrupa_proced_w);

    open c01;
	loop
	fetch c01 into
		nr_prescricao_w,
        nr_seq_prescr_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
    begin
        begin
            CALL start_fleury(
                'CANCELAR_FICHA_ITEM',
                nr_prescricao_w,
                nr_seq_prescr_w,
                cd_pessoa_fisica_p,
                cd_estabelecimento_p,
                nm_usuario_p,
                cd_setor_atendimento_p,
                cd_perfil_p,
                ''
            );
        exception
        when others then
            CALL gravar_log_lab_pragma(19392, 'start_fleury(
                CANCELAR_FICHA_ITEM,
                '||nr_prescricao_w||',
                '||nr_seq_prescr_w||',
                '||cd_pessoa_fisica_p||',
                '||cd_estabelecimento_p||',
                '||nm_usuario_p||',
                '||cd_setor_atendimento_p||',
                '||cd_perfil_p||'
            ); sqlerrm -> ' || sqlerrm || ' stack -> ' || dbms_utility.format_call_stack, nm_usuario_p);
        end;
    end;
    end loop;
	close c01;

end;
$body$
