CREATE OR REPLACE PROCEDURE status_solic_agenda_int_opme ( nr_seq_agenda_p bigint, cd_evento_log_p text, cd_fornecedor_p text, ds_descricao_p text, nm_usuario_p text) AS $body$
DECLARE



/*Procedure especifica para recebimento do solicitado com o cd_evento_log_p = '210'*/

nr_sequencia_w		bigint;
qt_existe_w		bigint;
ie_status_w		varchar(15);


BEGIN


if (cd_evento_log_p = '210') then
	begin

	select	nextval('agenda_pac_int_opme_log_seq')
	into STRICT	nr_sequencia_w
	;

	insert into agenda_pac_int_opme_log(
		nr_sequencia,		dt_atualizacao,
		nm_usuario,		dt_atualizacao_nrec,
		nm_usuario_nrec,	nr_seq_agenda,
		ds_descricao,		cd_fornecedor,
		cd_evento_log)
	values (	nr_sequencia_w,		clock_timestamp(),
		nm_usuario_p,		clock_timestamp(),
		nm_usuario_p,		nr_seq_agenda_p,
		null,			cd_fornecedor_p,
		cd_evento_log_p);

	select	count(*)
	into STRICT	qt_existe_w
	from	agenda_pac_opme a
	where	a.nr_seq_agenda	= nr_seq_agenda_p
	and	a.ie_origem_inf	= 'I'
	and	a.ie_integracao	= 'S'
	and	(a.cd_cgc IS NOT NULL AND a.cd_cgc::text <> '')
	and	not exists (
		SELECT	1
		from	agenda_pac_int_opme_log b
		where	b.nr_seq_agenda	= a.nr_seq_agenda
		and	b.cd_fornecedor	= a.cd_cgc);

	ie_status_w			:= '200';
	if (qt_existe_w = 0) then
		ie_status_w		:= '201';
	end if;

	update	agenda_paciente
	set	ie_opme_int_status		= ie_status_w,
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_agenda_p;

	end;
end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
