SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE substituir_comp_montagem_kit ( nr_seq_kit_estoque_p bigint, nr_sequencia_p bigint, cd_material_novo_p bigint, ds_motivo_p text, nm_usuario_p text) AS $body$
DECLARE


cd_material_w	integer;
ds_historico_w	varchar(2000);
ds_titulo_w	varchar(1000);


BEGIN

select	cd_material
into STRICT	cd_material_w
from	kit_estoque_comp
where	nr_seq_kit_estoque = nr_seq_kit_estoque_p
and	nr_sequencia = nr_sequencia_p;

--ds_titulo_w	:=	'Substituição de componente do kit';
ds_titulo_w	:=	wheb_mensagem_pck.get_texto(314050);
/*ds_historico_w	:=	substr('Substituido o componente ' || cd_material_w || ' - ' || obter_desc_material(cd_material_w) ||
			' pelo componente ' || cd_material_novo_p || ' - ' || obter_desc_material(cd_material_novo_p),1,1000);*/
ds_historico_w	:=	substr(wheb_mensagem_pck.get_texto(314051, 'CD_MATERIAL=' || cd_material_w 			 || ';' ||
							    'DS_MATERIAL=' || obter_desc_material(cd_material_w) || ';' ||
							    'CD_MATERIAL_NOVO=' || cd_material_novo_p		 || ';' ||
							    'DS_MATERIAL_NOVO=' || obter_desc_material(cd_material_novo_p)),1,1000);

CALL gravar_kit_estoque_hist(nr_seq_kit_estoque_p, ds_titulo_w, ds_historico_w, 'S', nm_usuario_p);

update	kit_estoque_comp
set	cd_material		= cd_material_novo_p,
	ie_gerado_barras	= 'N',
	cd_material_troca	= cd_material,
	nm_usuario_troca	= nm_usuario_p,
	ds_motivo_troca		= substr(ds_motivo_p,1,255)
where	nr_seq_kit_estoque	= nr_seq_kit_estoque_p
and	nr_sequencia		= nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
