CREATE OR REPLACE PROCEDURE substituir_guia_itens_conta ( nr_guia_nova_p text, nr_sequencia_p bigint, ie_tipo_item_p text) AS $body$
BEGIN

	if (ie_tipo_item_p = 'M') then
		update 	material_atend_paciente
		set 	nr_doc_convenio	= nr_guia_nova_p
		where 	nr_sequencia = nr_sequencia_p;
	elsif (ie_tipo_item_p = 'P') then
		update 	procedimento_paciente
		set 	nr_doc_convenio	= nr_guia_nova_p
		where 	nr_sequencia = nr_sequencia_p;
	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
