CREATE OR REPLACE PROCEDURE substituir_material_lp ( nr_lote_producao_p bigint, nr_sequencia_p bigint, nr_seq_subs_p bigint) AS $body$
DECLARE


cd_material_w		integer;
qt_dose_w		double precision;
qt_material_w		double precision;
qt_prevista_w		double precision;
cd_unidade_medida_w	varchar(30);


BEGIN

if (nr_lote_producao_p > 0) and (nr_sequencia_p > 0) and (nr_seq_subs_p > 0) then

	select	max(qt_prevista)
	into STRICT	qt_prevista_w
	from	lote_producao
	where	nr_lote_producao 		= 	nr_lote_producao_p;

	select	cd_material,
		qt_dose,
		qt_material,
		cd_unidade_medida
	into STRICT	cd_material_w,
		qt_dose_w,
		qt_material_w,
		cd_unidade_medida_w
	from	ficha_tecnica_comp_subs
	where	nr_sequencia			=	nr_seq_subs_p;

	update	lote_producao_comp
	set	cd_material			=	cd_material_w,
		qt_prevista			=	round((coalesce(qt_material_w,0) * coalesce(qt_prevista_w,0))::numeric,2),
		qt_unitaria			=	round((qt_material_w)::numeric,2),
		cd_material_subs		=	cd_material,
		qt_unitaria_subs		=	qt_unitaria,
		qt_dose_ficha_tecnica		=	qt_dose_w,
		cd_unid_med_ficha_tecnica	=	cd_unidade_medida_w
	where	nr_lote_producao		=	nr_lote_producao_p
	and	nr_sequencia			=	nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
