	'NR_ITEM=' || nr_item_solic_compra_p ||
	';CD_MATERIAL=' || cd_material_w ||
	';CD_NOVO_MATERIAL=' || cd_novo_material_p),
	'SM',
	nm_usuario_p);

commit;

end;
