SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE substituir_senha_conta ( nr_interno_conta_p bigint, cd_senha_ant_p text, cd_senha_nova_p text, ie_toda_senha_p text, ie_altera_atendimento_p text, nm_usuario_p text) AS $body$
DECLARE

 
nr_atendimento_w		bigint;
ie_atualizar_periodo_vig_w	varchar(1);
dt_periodo_inicial_w		timestamp;
dt_periodo_final_w		timestamp;
	

BEGIN 
 
ie_atualizar_periodo_vig_w	:= coalesce(obter_valor_param_usuario(67, 459, obter_perfil_ativo, nm_usuario_p,0),'N');
 
select	dt_periodo_inicial, 
	dt_periodo_final 
into STRICT	dt_periodo_inicial_w, 
	dt_periodo_final_w 
from	conta_paciente 
where	nr_interno_conta = nr_interno_conta_p;
 
 
if (ie_toda_senha_p = 'S') then	 
	update	procedimento_paciente 
	set	cd_senha		= cd_senha_nova_p 
	where	nr_interno_conta	= nr_interno_conta_p;
	 
	update	material_atend_paciente 
	set	cd_senha		= cd_senha_nova_p 
	where	nr_interno_conta	= nr_interno_conta_p;
else 
	update	procedimento_paciente 
	set	cd_senha		= cd_senha_nova_p 
	where	nr_interno_conta	= nr_interno_conta_p 
	and	cd_senha		= cd_senha_ant_p;
	 
	update	material_atend_paciente 
	set	cd_senha		= cd_senha_nova_p 
	where	nr_interno_conta	= nr_interno_conta_p 
	and	cd_senha		= cd_senha_ant_p;
end if;
 
if (ie_altera_atendimento_p	= 'S') then 
	 
	select 	max(nr_atendimento) 
	into STRICT	nr_atendimento_w 
	from 	conta_paciente 
	where 	nr_interno_conta = nr_interno_conta_p;
 
	if (ie_atualizar_periodo_vig_w = 'S') then 
	 
		update	atend_categoria_convenio a 
		set	a.cd_senha	 		= cd_senha_nova_p, 
			a.nm_usuario		= nm_usuario_p, 
			a.dt_atualizacao	= clock_timestamp() 
		where	a.nr_seq_interno	 =	(SELECT	max(x.nr_seq_interno) 
							from	atend_categoria_convenio x 
							where	x.nr_atendimento = nr_atendimento_w 
							and	x.dt_inicio_vigencia between dt_periodo_inicial_w and fim_dia(dt_periodo_final_w) 
							and	coalesce(x.dt_final_vigencia, x.dt_inicio_vigencia) between dt_periodo_inicial_w and fim_dia(dt_periodo_final_w));		
					 
	elsif (ie_atualizar_periodo_vig_w = 'I') then 
	 
		update	atend_categoria_convenio a 
		set	a.cd_senha	 	= cd_senha_nova_p, 
			a.nm_usuario		= nm_usuario_p, 
			a.dt_atualizacao	= clock_timestamp() 
		where	a.nr_atendimento 	= nr_atendimento_w 
		and 	a.dt_inicio_vigencia  = dt_periodo_inicial_w 
		and	((a.dt_final_vigencia IS NOT NULL AND a.dt_final_vigencia::text <> '' AND a.dt_final_vigencia = dt_periodo_final_w) or (coalesce(a.dt_final_vigencia::text, '') = ''));
		 
	elsif (ie_atualizar_periodo_vig_w = 'F') then 
	 
		update	atend_categoria_convenio a 
		set	a.cd_senha	 	= cd_senha_nova_p, 
			a.nm_usuario		= nm_usuario_p, 
			a.dt_atualizacao	= clock_timestamp() 
		where	a.nr_atendimento 	= nr_atendimento_w 
		and 	a.dt_inicio_vigencia  = dt_periodo_inicial_w;
						 
	else 
	 
		update	atend_categoria_convenio a 
		set	a.cd_senha	 		= cd_senha_nova_p, 
			a.nm_usuario		= nm_usuario_p, 
			a.dt_atualizacao	= clock_timestamp() 
		where	a.nr_seq_interno	 =	(SELECT	max(x.nr_seq_interno) 
							from	atend_categoria_convenio x 
							where	x.nr_atendimento = nr_atendimento_w);
	 
	end if;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE substituir_senha_conta ( nr_interno_conta_p bigint, cd_senha_ant_p text, cd_senha_nova_p text, ie_toda_senha_p text, ie_altera_atendimento_p text, nm_usuario_p text) FROM PUBLIC;

