CREATE OR REPLACE PROCEDURE sug_enviar_ci_historico ( nr_seq_sugestao_p bigint, nr_seq_historico_p bigint, nm_usuario_p text, nm_usuario_destino_p text, nr_seq_classif_p text) AS $body$
DECLARE


ds_historico_w 		text;
ds_classif_w		varchar(255);
nm_usuaio_w		varchar(255);
nm_usuario_adic_w	varchar(2000);

c01 CURSOR FOR
SELECT	obter_usuario_pessoa(a.cd_pessoa_destino)
from	sug_sugestao_dest_adic a,
	sug_sugestao b
where	a.nr_seq_sugestao = b.nr_sequencia
and	b.nr_sequencia = nr_seq_sugestao_p
and	(obter_usuario_pessoa(a.cd_pessoa_destino) IS NOT NULL AND (obter_usuario_pessoa(a.cd_pessoa_destino))::text <> '');


BEGIN

nm_usuario_adic_w := '';

select	ds_historico
into STRICT	ds_historico_w
from	sug_sugestao_hist
where	nr_sequencia = nr_seq_historico_p;

nm_usuario_adic_w := replace(nm_usuario_destino_p,',','');

open C01;
loop
fetch C01 into
	nm_usuaio_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	nm_usuario_adic_w := nm_usuario_adic_w ||','|| nm_usuaio_w;
	end;
end loop;
close C01;

select	obter_descricao_padrao('SUG_CLASSIFICACAO','DS_CLASSIFICACAO',nr_seq_classif) ds_clssif
into STRICT	ds_classif_w
from	sug_sugestao
where	nr_sequencia = nr_seq_sugestao_p;

ds_historico_w	:= wheb_rtf_pck.get_texto_rtf(wheb_mensagem_pck.get_texto(303978,'NR_SEQ_SUGESTAO='||nr_seq_sugestao_p||
										';DS_CLASSIF=' || ds_classif_w ||
										';DS_HISTORICO=' || ds_historico_w));

insert into 	comunic_interna(	dt_comunicado,
					ds_titulo,
					ds_comunicado,
					nm_usuario,
					dt_atualizacao,
					ie_geral,
					nm_usuario_destino,
					cd_perfil,
					nr_sequencia,
					ie_gerencial,
					nr_seq_classif,
					ds_perfil_adicional,
					cd_setor_destino,
					cd_estab_destino,
					ds_setor_adicional,
					dt_liberacao,
					ds_grupo,
					nm_usuario_oculto)
		values (	clock_timestamp(),
					wheb_mensagem_pck.get_texto(303991,'NR_SEQ_SUGESTAO='||nr_seq_sugestao_p),
					ds_historico_w,
					nm_usuario_p,
					clock_timestamp(),
					'N',
					nm_usuario_adic_w,
					null,
					nextval('comunic_interna_seq'),
					'N',
					nr_seq_classif_p,
					'',
					null,
					'',
					'',
					clock_timestamp(),
					'',
					'');
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
