CREATE OR REPLACE PROCEDURE sum_atualizar_classif_diag ( nr_seq_diag_p bigint, ie_classif_diag_p text, nm_usuario_p text) AS $body$
DECLARE


ie_classif_diag_w	varchar(1);


BEGIN

if (nr_seq_diag_p IS NOT NULL AND nr_seq_diag_p::text <> '') then
	begin
	if (coalesce(ie_classif_diag_p::text, '') = '') then
		select	max(a.ie_tipo_diagnostico)
		into STRICT	ie_classif_diag_w
		from	diagnostico_classif_adic a
		where	a.cd_doenca = (	SELECT	max(b.cd_doenca)
					from	diagnostico_doenca b
					where	b.nr_seq_interno = nr_seq_diag_p);
	else
		ie_classif_diag_w := ie_classif_diag_p;
	end if;

	update	diagnostico_doenca
	set	ie_tipo_diag_classif = ie_classif_diag_w,
		nm_usuario = nm_usuario_p
	where	nr_seq_interno = nr_seq_diag_p;
	end;
end if;

commit;

end;
