CREATE OR REPLACE PROCEDURE supplypoint_gerar_req_auto ( nr_requisicao_p bigint, cd_estabelecimento_p bigint, cd_material_p bigint, qt_material_p bigint, cd_local_destino_p bigint, ie_liberar_p text, nr_sequencia_p INOUT bigint, ds_erro_p INOUT text) AS $body$
DECLARE

 
nr_sequencia_w			bigint;
cd_unidade_medida_w		varchar(30);
cd_unidade_medida_estoque_w	varchar(30);
qt_conv_estoque_cons_w		double precision;
qt_estoque_w			double precision;
ds_erro_w			varchar(2000);
nr_seq_erro_w			bigint;
nr_item_requisicao_w		bigint;
qt_atendida_w			double precision;
cd_pessoa_requisitante_w	bigint;
dt_liberacao_w			timestamp;
cd_pessoa_requisicao_w		varchar(10);
cd_operacao_estoque_w		smallint;
cd_local_estoque_w		smallint;
cd_local_estoque_req_w		smallint;
nr_seq_req_w			bigint;
nm_usuario_w			usuario.nm_usuario%type;
cd_material_w			material.cd_material%type;
cd_material_ww			material.cd_material%type;
qt_saldo_w			double precision := 0;
qt_saldo_ww			double precision := 0;
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;

c01 CURSOR FOR 
	SELECT	coalesce(substr(wheb_mensagem_pck.get_texto(314232) || cd_material || ' - ' || ds_consistencia || '.',1,2000),null) 
	from	requisicao_mat_consist 
	where	nr_requisicao = nr_seq_req_w;


BEGIN 
cd_estabelecimento_w := cd_estabelecimento_p;
nr_seq_req_w := nr_requisicao_p;
cd_material_w := cd_material_p;
 
if (cd_estabelecimento_w = 0) and (coalesce(cd_local_destino_p,0) > 0) then 
	select	coalesce(max(cd_estabelecimento),0) 
	into STRICT	cd_estabelecimento_w 
	from	local_estoque 
	where	cd_local_estoque = cd_local_destino_p;
end if;
 
if (cd_estabelecimento_w = 0) then 
	select	max(cd_estabelecimento) 
	into STRICT	cd_estabelecimento_w 
	from	parametros_farmacia 
	where	(cd_pessoa_requisicao IS NOT NULL AND cd_pessoa_requisicao::text <> '');
end if;
 
select	cd_pessoa_requisicao, 
	cd_operacao_estoque, 
	cd_local_estoque, 
	cd_local_estoque_req, 
	obter_usuario_pf(cd_pessoa_requisicao) 
into STRICT	cd_pessoa_requisicao_w, 
	cd_operacao_estoque_w, 
	cd_local_estoque_w, 
	cd_local_estoque_req_w, 
	nm_usuario_w 
from	parametros_farmacia 
where	cd_estabelecimento = cd_estabelecimento_w;
 
if (coalesce(nr_seq_req_w::text, '') = '') or (nr_seq_req_w = 0) then 
	begin 
	select	nextval('requisicao_seq') 
	into STRICT	nr_seq_req_w 
	;
	 
	nr_sequencia_p := nr_seq_req_w;
	 
	insert into requisicao_material( 
		nr_requisicao, 
		cd_estabelecimento, 
		cd_local_estoque, 
		dt_solicitacao_requisicao, 
		dt_atualizacao, 
		nm_usuario, 
		cd_operacao_estoque, 
		cd_pessoa_requisitante, 
		cd_local_estoque_destino, 
		nm_usuario_lib, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		ie_geracao, 
		ie_urgente) 
	values (	nr_seq_req_w, 
		cd_estabelecimento_w, 
		cd_local_estoque_w, 
		clock_timestamp(), 
		clock_timestamp(), 
		nm_usuario_w, 
		cd_operacao_estoque_w, 
		cd_pessoa_requisicao_w, 
		cd_local_estoque_req_w, 
		nm_usuario_w, 
		clock_timestamp(), 
		nm_usuario_w, 
		'I', 
		'N');
	end;
end if;
 
if (nr_seq_req_w > 0) then 
	begin 
	select	coalesce(max(nr_sequencia),0) + 1 
	into STRICT	nr_sequencia_w 
	from	item_requisicao_material 
	where	nr_requisicao = nr_seq_req_w;
	 
	cd_unidade_medida_w 		:= obter_dados_material_estab(cd_material_w,cd_estabelecimento_w,'UMC');
	cd_unidade_medida_estoque_w 	:= obter_dados_material_estab(cd_material_w,cd_estabelecimento_w,'UME');
	qt_conv_estoque_cons_w 		:= obter_conversao_material(cd_material_w,'CE');
	 
	insert into item_requisicao_material( 
		nr_requisicao, 
		nr_sequencia, 
		cd_estabelecimento, 
		cd_material, 
		qt_material_requisitada, 
		vl_material, 
		dt_atualizacao, 
		nm_usuario, 
		cd_unidade_medida, 
		qt_estoque, 
		cd_unidade_medida_estoque) 
	values ( nr_seq_req_w, 
		nr_sequencia_w, 
		cd_estabelecimento_w, 
		cd_material_w, 
		qt_material_p, 
		0, 
		clock_timestamp(), 
		nm_usuario_w, 
		cd_unidade_medida_w, 
		qt_estoque_w, 
		cd_unidade_medida_estoque_w);
	end;
end if;
 
commit;
 
if (ie_liberar_p = 'S') then 
	ds_erro_p := consistir_requisicao(nr_seq_req_w, nm_usuario_w, cd_local_estoque_req_w, null, 'N', 'N', 'N', 'N', 'S', 'N', 'N', cd_operacao_estoque_w, ds_erro_p);
	 
	select	dt_liberacao 
	into STRICT	dt_liberacao_w 
	from	requisicao_material 
	where	nr_requisicao = nr_seq_req_w;
	 
	if (coalesce(dt_liberacao_w::text, '') = '') then 
		open c01;
		loop 
		fetch c01 into 
			ds_erro_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin 
			ds_erro_p := ds_erro_P || ds_erro_w;
			end;
		end loop;
		close c01;
	end if;
end if;
 
commit;
 
end;
$body$
